pragma solidity 0.8.18;

interface IStakingStrategy {
    function stake(address _itemAddress, uint256 _itemId, bytes memory _payload) external;
}","keccak256":"0x2e53f5319fb3596800810274c35761f8d9ee08e5a2753630873fd6efc637e597","license":"UNLICENSED"},"contracts/interfaces/ITreasury.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
